            const sosmed = value.getAttribute('sosmed');
            if (sosmed == 'ig') {
                value.addEventListener('click', function() {
                    open('https://www.instagram.com/ardistory___');
                });
            } else if (sosmed == 'tt') {
                value.addEventListener('click', function() {
                    open('https://www.tiktok.com/@ardistory___');
                });
            } else if (sosmed == 'yt') {
                value.addEventListener('click', function() {
                    open('https://www.youtube.com/@ardistory___');
                });
            } else if (sosmed == 'fb') {
                value.addEventListener('click', function() {
                    open('https://www.facebook.com/ardi.corps');
                });
            } else if (sosmed == 'github') {
                value.addEventListener('click', function() {
                    open('https://github.com/ardistory');
                });
            }
        });

        const tryChat = document.querySelector('#tryChat');
        tryChat.addEventListener('click', function() {
            open('./chat');
        });

        const openQuran = document.querySelector('#openQuran');
        openQuran.addEventListener('click', function() {
            open('./alquran-digital');
        });
    </script>
</body>

</html>